import React, { useState } from 'react';
import '../css/TaskForm.css';
import { v4 as uuidv4 } from 'uuid';
import { AiOutlineSearch, AiOutlineUnorderedList } from "react-icons/ai";
function TareaFormulario(props) {

    const [input, setInput] = useState('');

    const manejarCambio = e => {
        setInput(e.target.value);
    }

    const manejarEnvio = e => {
        e.preventDefault();

        const tareaNueva = {
            id: uuidv4(),
            texto: input,
            completada: false
        }

        props.onSubmit(tareaNueva);

    }
    const buscarTarea = e => {
        e.preventDefault();
        props.buscar(input);
    }

    return (
        <form
            className='tarea-formulario'
            onSubmit={manejarEnvio}>
            <input
                className='tarea-input'
                type='text'
                placeholder='Escribe una Tarea'
                name='texto'
                onChange={manejarCambio}
            />
            <button
                className='tarea-boton'
                onClick={buscarTarea} title='Buscar Tarea'>
                <AiOutlineSearch className='tarea-icono' />
            </button>
            <button className='tarea-boton' title='Agregar Tarea'>
                <AiOutlineUnorderedList className='tarea-icono' />
            </button>
        </form>
    );
}

export default TareaFormulario;
