import React, { useState, useEffect } from 'react';
import TareaFormulario from './TaskForm';
import Tarea from './Task';
import '../css/TaskList.css';


function ListaDeTareas() {

    const [tareas, setTareas] = useState([]);

    const traerTareas = async () => {
        try {
            const response = await fetch("api/Task/Index");
            const data = await response.json()
            setTareas(data);
        } catch (error) {
            console.error(error);
        }
    }
    const agregarTarea = tarea => {
        if (tarea.texto.trim()) {
            tarea.texto = tarea.texto.trim();
            var formdata = new FormData();
            formdata.append("Texto", tarea.texto);

            var requestOptions = {
                method: 'POST',
                body: formdata,
                redirect: 'follow'
            };

            fetch("api/Task/Create", requestOptions)
                .then(response => response.text())
                .then(result => traerTareas())
                .catch(error => console.log('error', error));

        }
    }

    const eliminarTarea = id => {
        if (!confirm("Esta seguro que desea elimianr esta tarea"))
            return;
        else {
            fetch('api/Task/Delete/' + id, {
                method: 'delete'
            }).then(data => {
                traerTareas();
            });
        }
    }

    const completarTarea = id => {
        const tareaEditar = tareas.find(tarea => tarea.id == id);
        if (tareaEditar.id === id) {
            tareaEditar.completada = !tareaEditar.completada;
        }

        var formdata = new FormData();
        formdata.append("Texto", tareaEditar.texto);
        formdata.append("Completada", tareaEditar.completada);
        formdata.append("Id", tareaEditar.id);
        var requestOptions = {
            method: 'Put',
            body: formdata,
            redirect: 'follow'
        };

        fetch("api/Task/Edit", requestOptions)
            .then(response => response.text())
            .then(result => traerTareas())
            .catch(error => console.log('error', error));

    }
    const editarTarea = id => {
        const tareasActualizadas = tareas.map(tarea => {
            if (tarea.id === id) {
                tarea.completada = !tarea.completada;
            }
            return tarea;
        });
        setTareas(tareasActualizadas);
    }
    const buscarTarea = text => {
        const tareasFiltradas = tareas.filter(x => x.texto.includes(text))
        setTareas(tareasFiltradas);
    }

    useEffect(() => {
        traerTareas();
    }, []);

    return (
        <>
            <TareaFormulario onSubmit={agregarTarea} onClick={buscarTarea} />
            <div className='tareas-lista-contenedor'>
                {
                    tareas.map((tarea) =>
                        <Tarea
                            key={tarea.id}
                            id={tarea.id}
                            texto={tarea.texto}
                            completada={tarea.completada}
                            completarTarea={completarTarea}
                            eliminarTarea={eliminarTarea} />
                    )
                }
            </div>
        </>
    );
}




export default ListaDeTareas;
