import React, { useState } from 'react';
import '../css/Task.css';
import { AiOutlineCloseCircle, AiFillEdit, AiOutlineCheckCircle, AiOutlineClose } from "react-icons/ai";

function Task({ id, texto, completada, editar, completeTask, deleteTask, taskEdit, confirmEdit, cancelEdit }) {
    const [input, setInput] = useState('');
    const changeInput = e => {
        setInput(e.target.value);
    }
    if (editar) {
        return (
            <div className='tarea-contenedor'>
                <div>
                    <input type="text" className='tarea-input' name='texto' placeholder={texto} onChange={changeInput}/>
                </div>
                <div className='tarea-contenedor-iconos'
                    onClick={() => confirmEdit(id,input)}>
                    <AiOutlineCheckCircle className='tarea-icono' />
                </div>
                <div
                    className='tarea-contenedor-iconos'
                    onClick={() => cancelEdit(id)}>
                    <AiOutlineClose className='tarea-icono' />
                </div>
            </div>

        );
    }
    else {
        return (
            <div className={completada ? 'tarea-contenedor completada' : 'tarea-contenedor'}>
                <div
                    className='tarea-texto'
                    onClick={() => completeTask(id)}>
                    {texto}
                </div>
                <div className='tarea-contenedor-iconos'
                    onClick={() => taskEdit(id)}>
                    <AiFillEdit className='tarea-icono' />
                </div>
                <div
                    className='tarea-contenedor-iconos'
                    onClick={() => deleteTask(id)}>
                    <AiOutlineCloseCircle className='tarea-icono' />
                </div>
            </div>
        );
    }



}

export default Task;
