import React, { useState } from 'react';
import '../css/Task.css';
import { AiOutlineCloseCircle, AiFillEdit, AiOutlineCheckCircle, AiOutlineClose } from "react-icons/ai";

function Tarea({ id, texto, completada, editar, completarTarea, eliminarTarea, editarTarea, confirmarEditar, cancelarEditar }) {
    const [input, setInput] = useState('');
    const manejarCambioInput = e => {
        setInput(e.target.value);
    }
    if (editar) {
        return (
            <div className='tarea-contenedor'>
                <div>
                    <input type="text" className='tarea-input' name='texto'  onChange={manejarCambioInput}/>
                </div>
                <div className='tarea-contenedor-iconos'
                    onClick={() => confirmarEditar(id)}>
                    <AiOutlineCheckCircle className='tarea-icono' />
                </div>
                <div
                    className='tarea-contenedor-iconos'
                    onClick={() => cancelarEditar()}>
                    <AiOutlineClose className='tarea-icono' />
                </div>
            </div>

        );
    }
    else {
        return (
            <div className={completada ? 'tarea-contenedor completada' : 'tarea-contenedor'}>
                <div
                    className='tarea-texto'
                    onClick={() => completarTarea(id)}>
                    {texto}
                </div>
                <div className='tarea-contenedor-iconos'
                    onClick={() => editarTarea(id)}>
                    <AiFillEdit className='tarea-icono' />
                </div>
                <div
                    className='tarea-contenedor-iconos'
                    onClick={() => eliminarTarea(id)}>
                    <AiOutlineCloseCircle className='tarea-icono' />
                </div>
            </div>
        );
    }



}

export default Tarea;
